changeSet.sql:
alter table "User" add column creator_user int;;
update "User" f set creator_user = ( select min("ID") from "User" p where p.valid_time_start <= f.valid_time_start and f.valid_time_start < p.valid_time_end and p.transaction_time_end = 'infinity');;
create or replace function user_creator_user_ref_user() returns trigger language plpgsql as $$ declare count int; begin select * into count from "User" f where f.transaction_time_end = 'infinity' and ( not exists( select * from "User" p where f.creator_user = p."ID" and p.transaction_time_end = 'infinity' and p.valid_time_start <= f.valid_time_start and f.valid_time_start < p.valid_time_end ) or not exists( select * from "User" p where f.creator_user = p."ID" and p.transaction_time_end = 'infinity' and p.valid_time_start < f.valid_time_end and f.valid_time_end <= p.valid_time_end ) ); if count != 0 then raise check_violation; end if; return new; end; $$; create constraint trigger user_creator_user_ref_user after insert or update or delete on "User" deferrable initially deferred for each row execute function user_creator_user_ref_user();

